function generate(){url="https://api.adviceslip.com/advice",fetch(url,{cache:"no-cache"}).then((e=>e.json())).then((e=>{let n=document.getElementById("advice-id"),t=document.getElementById("advice"),d=e.slip.id,i=e.slip.advice;n.innerHTML=d,t.innerHTML=i}))}window.addEventListener("load",(()=>{generate(),document.getElementById("generate").addEventListener("click",(()=>{generate()}))}));